import React, { useState, useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { FaList, FaEnvelope } from 'react-icons/fa';
import styles from './Quest.module.css';

const Quest = ({ userId, show, onClose, questData }) => {
  const [selectedMission, setSelectedMission] = useState(null);
  const [activeTab, setActiveTab] = useState('list'); // 'list' or 'message'
  const [displayedMessages, setDisplayedMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const messagesLoaded = useRef(false);
  const questContainerRef = useRef(null);

  useEffect(() => {
    if (activeTab === 'message' && !messagesLoaded.current) {
      setDisplayedMessages([]);
      setIsTyping(true);
      questData.scenario[0].split(',').forEach((message, index) => {
        setTimeout(() => {
          setIsTyping(false);
          setDisplayedMessages(prevMessages => [...prevMessages, message.trim()]);
          if (index < questData.scenario[0].split(',').length - 1) {
            setIsTyping(true);
          }
          if (index === questData.scenario[0].split(',').length - 1) {
            messagesLoaded.current = true; // 메시지가 모두 로드되었음을 표시
          }
        }, (index + 1) * 2000);
      });
    }
  }, [activeTab, questData]);

  useEffect(() => {
    const container = questContainerRef.current;
    let startY;
    let scrollTop;

    const onMouseDown = (e) => {
      startY = e.pageY - container.offsetTop;
      scrollTop = container.scrollTop;
      container.style.cursor = 'grabbing';
    };

    const onMouseMove = (e) => {
      if (startY !== undefined) {
        const y = e.pageY - container.offsetTop;
        const walk = y - startY;
        container.scrollTop = scrollTop - walk;
      }
    };

    const onMouseUp = () => {
      startY = undefined;
      container.style.cursor = 'grab';
    };

    if (container) {
      container.addEventListener('mousedown', onMouseDown);
      container.addEventListener('mousemove', onMouseMove);
      container.addEventListener('mouseup', onMouseUp);
      container.addEventListener('mouseleave', onMouseUp);
    }

    return () => {
      if (container) {
        container.removeEventListener('mousedown', onMouseDown);
        container.removeEventListener('mousemove', onMouseMove);
        container.removeEventListener('mouseup', onMouseUp);
        container.removeEventListener('mouseleave', onMouseUp);
      }
    };
  }, []);

  if (!show) {
    return null;
  }

  const renderMissionList = () => (
    <div className={styles.missionList}>
      <h2>MISSION</h2>
      <ul>
        {questData.scenario[0].split(',').map((task, index) => (
          <li key={index} onClick={() => setSelectedMission(task.trim())}>{task.trim()}</li>
        ))}
      </ul>
    </div>
  );

  const renderMessageList = () => (
    <div className={styles.messageList}>
      <div className={styles.profileHeader}>
        <img src="https://i.imgur.com/FuHIa08.jpeg" alt="Profile" className={styles.profileImage} />
        <h2>010-XXXX-XXXX</h2>
      </div>
      {displayedMessages.map((message, index) => (
        <div key={index} className={styles.message}>
          <p>{message}</p>
        </div>
      ))}
      {isTyping && <TypingIndicator />}
    </div>
  );

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    if (tab === 'message' && !messagesLoaded.current) {
      messagesLoaded.current = false; // 메시지를 다시 로드하지 않도록 설정
    }
  };

  return (
    <div className={styles.modalOverlay} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <div className={styles.ipad}>
          <div className={styles.screen}>
            <div className={styles.tabContainer}>
              <div className={styles.tab} onClick={() => handleTabClick('list')}>
                <FaList size={24} />
              </div>
              <div className={styles.tab} onClick={() => handleTabClick('message')}>
                <FaEnvelope size={24} />
              </div>
            </div>
            <div className={styles.questContainer} ref={questContainerRef}>
              {activeTab === 'list' && questData && renderMissionList()}
              {activeTab === 'message' && questData && renderMessageList()}
              {selectedMission && (
                <div className={styles.missionDetails}>
                  <h2>{selectedMission}</h2>
                  <button onClick={() => setSelectedMission(null)}>Back</button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const TypingIndicator = () => (
  <div className={styles.typingIndicator}>
    <div></div>
    <div></div>
    <div></div>
  </div>
);

Quest.propTypes = {
  userId: PropTypes.string.isRequired,
  show: PropTypes.bool.isRequired,
  onClose: PropTypes.func.isRequired,
  questData: PropTypes.object.isRequired, // 추가된 prop 타입
};

export default Quest;
