import { Terminal } from "@xterm/xterm";
import "../../node_modules/@xterm/xterm/css/xterm.css";
import React,{useRef,useEffect,useState} from "react";
import { Termi } from "../pages/mainPages/temp/terminal";
import DirectoryTree from "./fileSystem";

const TermPage=()=> {
    const terminalRef = useRef(null); // DOM 요소 참조를 위한 ref
    useEffect(() => {
      if(terminalRef.current) {
        const term = new Terminal();
        Termi(term,terminalRef.current);
        return () => {
            term.dispose(); // Terminal 인스턴스 정리
          };
      }
    }, []);
    const filesysRef = useRef(null);
    const [fileSystemObject, setFileSystemObject] = useState(null); // 파일 시스템 구조를 상태로 관리
    useEffect(()=>{
      const fs = new FileSystem();
      // 여기에서 FileSystem 인스턴스를 사용하여 파일 시스템 구조를 초기화합니다.
      fs.createDirectory("/root");
      fs.createDirectory("/tmp");
      fs.createDirectory("/home/user");
      fs.createFile("/home/user/file1.txt");
      fs.createFile("/home/user/file2.txt");
      fs.createDirectory("/home/user/documents");
      fs.createFile("/home/user/documents/document1.txt");

      // FileSystem에서 구조를 가져와서 상태에 설정
      // 여기서는 FileSystem 구현이 객체 형태의 파일 시스템 구조를 반환한다고 가정합니다.
      const structure = fs.printFileSystem(); // 이 메소드가 객체 형태의 파일 시스템 구조를 반환한다고 가정
      setFileSystemObject(structure);

    },[]);
    return (<>
                {fileSystemObject && <DirectoryTree directory={fileSystemObject} />}
                <div ref={terminalRef} style={{width: "100%",height: "100%"}}></div>
</>
    );
}
export default TermPage;
