import { useState, useEffect } from 'react';
import io from 'socket.io-client';


function Termi(terminal2,elements) {

    const token = localStorage.getItem('accessToken');
    const socket = io('http://localhost:4000/term',{
        query: { token }
    }); // NestJS 서버 주소

    // const [message, setMessage] = useState('');
    // const [chat, setChat] = useState([]);

    // useEffect(() => {
    //   socket.on('message', (payload) => {
    //     setChat([...chat, payload]);
    //   });
    // }, [chat]);

    // const sendMessage = () => {
    //   socket.emit('message', message);
    //   setMessage('');
    // };

    let message = '';
    let chat = [];

    socket.on('message',(payload)=>{
        chat = payload;
        console.log(chat);
    });
    const sendMessage = () => {
        socket.emit('message',message);
        message = '';
    }

    const terminal = terminal2;
    terminal.open(elements);
    let currentInput = '';
    terminal.write('Welcome to NetRunner!\r\n');
    let currentPath = '/home/user';
    prompt('',currentPath);
    terminal.onKey(({ key, domEvent }) => {
        const char = key;
        if (domEvent.keyCode === 13) {  // Enter key
            terminal.writeln('');

            // setMessage(currentInput);
            // sendMessage();
            // terminal.write(chat);

            message += currentInput;
            sendMessage();
            terminal.write(chat);

            currentInput = '';
            prompt('',currentPath);
        } else if (domEvent.keyCode === 8) {
            // Backspace 처리
            if (currentInput.length > 0) {
                terminal.write('\b \b'); // 터미널에서 문자를 제거
                currentInput = currentInput.slice(0, -1);
            }
        } else {
            currentInput += char;
            terminal.write(char);
        }
    });
    function prompt(ip, location) {
        if (location == "/root") {
            location = '~';
        }
        terminal.write(`${ip}root@root[${location}]$`);
    }
}

export { Termi };
