import {SocketResult} from "./socket";

let terminal = null;
let directoryViewer = null;
const sockets = new SocketResult();
export const TerminalInteraction = {
  //터미널 객체 설정
  setTerminal: (term) => {
    terminal = term;
  },

  //gui페이지 설정
  setDirectoryViewer: (viewer) => {
    directoryViewer = viewer;
  },

  //터미널->GUI
  handleTerminalInput: (data) => {
    // 터미널 창에서 입력된 명령어를 DirectoryViewer에 전달
    sockets.sendMessage(data);
    sockets.getMessage((data) => {
      directoryViewer.updateDirectoryContent(data);
    });

  },

  //GUI->터미널
  appendToTerminal: (text) => {
    // DirectoryViewer에서 전달받은 텍스트를 터미널 창에 입력
    terminal.write(text);
    terminal.write('\r\n');
    sockets.sendMessage(text);
    sockets.getMessage((text) => {
      const temp1 = text;
      const temp2 = text;
      const regex = /\[(.*?)\]/g;
      const files = temp1.replace(regex, '$1').split(' ');
      const regex2 = /\[(directory|file)\]/g;
      const result =  temp2.match(regex2);
      const regex3 = /\[(.*?)\]/g;
      const filestype = result.map(item => item.replace(regex3, '$1'));
      const setDir = {files,filestype};
      directoryViewer.setContents(setDir);
    });
  }
};
