import { Terminal } from "@xterm/xterm";
import "../../node_modules/@xterm/xterm/css/xterm.css";
import React, { useRef, useEffect, useState } from "react";
import { SocketResult } from "./socket";

const TermPage = ({ /*text, onTerminalInput*/ }) => {
  const terminalRef = useRef();
  const [terminal, setTerminal] = useState();

  useEffect(() => {
    if (!terminal && terminalRef.current) {
      const newTerminal = new Terminal();
      // newTerminal.onData((data) => {
      //   onTerminalInput(data);
      // });
      newTerminal.open(terminalRef.current);
      setTerminal(newTerminal);

      const sockets = new SocketResult();
      let currentInput = "";
      let currentPath = "/home/user";

      newTerminal.write("Welcome to NetRunner!\r\n");
      prompt(newTerminal, currentPath);

      newTerminal.onKey(({ key, domEvent }) => {
        const char = key;
        if (domEvent.keyCode === 13) {
          // Enter key
          const message = currentInput;
          newTerminal.writeln("");
          sockets.sendMessage(message, currentPath);
          prompt(newTerminal, currentPath);
          sockets.getMessage((chat) => {
            newTerminal.write(chat);
            newTerminal.writeln("");
            prompt(newTerminal, currentPath);
          });
          currentInput = "";
        } else if (domEvent.keyCode === 8) {
          // Backspace 처리
          if (currentInput.length > 0) {
            newTerminal.write("\b \b"); // 터미널에서 문자를 제거
            currentInput = currentInput.slice(0, -1);
          }
        } else {
          currentInput += char;
          newTerminal.write(char);
        }
      });

      return () => {
        newTerminal.dispose();
      };
    }

    // if (terminal && text) {
    //   terminal.write(text);
    //   terminal.writeln("");
    // }
  }, [/*text, onTerminalInput,*/ terminal, terminalRef]);

  return <div ref={terminalRef} style={{ width: "100%", height: "100%"}}/>;
};

function prompt(terminal, location) {
  if (location === "/root") {
    location = "~";
  }
  terminal.write(`root@root[${location}]$`);
}

export default TermPage;
