import { Terminal } from "@xterm/xterm";
import "../../../node_modules/@xterm/xterm/css/xterm.css";
import React, { useRef, useEffect } from "react";
import { Termi } from "./termsocket";
import TerminalInteraction from './TerminalInteraction';
import { SocketResult } from "../../socket/socket";

const TermPage = () => {
  const termRef = useRef(null); // 터미널 요소를 참조하기 위한 ref
  const socketRoomId = Math.floor(100000 + Math.random() * 900000).toString(); // 임의의 소켓 방 ID 생성
  const socket = new SocketResult(); // 소켓 객체 생성
  socket.joinRoom(socketRoomId); // 소켓 방에 참여

  useEffect(() => {
    if (termRef.current) {
      const term = new Terminal(); // 새로운 터미널 객체 생성
      Termi(term, termRef.current, socket.getRoomId()); // 터미널 초기화 함수 호출
      TerminalInteraction.setTerminal(term); // TerminalInteraction에 터미널 설정
      return () => {
        term.dispose(); // 컴포넌트 언마운트 시 터미널 객체 해제
      };
    }
  }, [socket]);

  return (
    <>
      <div ref={termRef} className="terminal-container" /> {/* 터미널 요소 */}
    </>
  );
}

export default TermPage;
