import { React, useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from "./loading.module.css";

function Loading() {
    const bar = useRef(null);
    const [percentage, setPercentage] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const interval = setInterval(() => {
            setPercentage((prevPercentage) => {
                const newPercentage = prevPercentage + 0.01;
                if (newPercentage >= 1) {
                    clearInterval(interval);
                    // ÏßÑÌñâÎ•†Ïù¥ 100%Ïóê ÎèÑÎã¨ÌñàÏùÑ Îïå Ï∂îÍ∞Ä ÎèôÏûë
                }
                return newPercentage;
            });
        }, 50);

        return () => clearInterval(interval);
    }, []);

    useEffect(() => {
        if (percentage >= 1) {
            // 'done' ÌÅ¥ÎûòÏä§Î•º Ï∂îÍ∞ÄÌïòÎäî ÎåÄÏã†, ÏÉÅÌÉúÎ•º ÏÇ¨Ïö©Ìï¥ Î°úÎî© ÏôÑÎ£åÎ•º Ï≤òÎ¶¨Ìï©ÎãàÎã§.
            setTimeout(() => {
                navigate('/main');
            }, 2000);
        }
    }, [percentage, navigate]);

    return (
        <div ref={bar} className={styles.progress_bar} style={{ '--progress': `${percentage * 100}%` }}>
            <div className={styles.box}>
                {/* Î°úÎî© Ï§ëÍ≥º Î°úÎî© ÏôÑÎ£å ÌÖçÏä§Ìä∏Î•º Ï°∞Í±¥Î∂ÄÎ°ú Î†åÎçîÎßÅ */}
                {percentage < 1 ? (
                    <p className={styles.loading_text}>Î°úÎî©Ï§ë...</p>
                ) : (
                    <p className={styles.done_text}>Î°úÎî© ÏôÑÎ£å üéâ</p>
                )}
                <div className={styles.box_front} style={{ width: `${percentage * 100}%` }}></div>
                <div className={styles.box_bottom}></div>
            </div>
        </div>
    );
}

export default Loading;
