// Modal.js
import React from 'react';
import ReactDOM from 'react-dom';

const Modal = ({ isOpen, closeModal, children }) => {
    const modalRoot = document.getElementById('modal-root');
    if (!modalRoot) { console.log(`모달루트 상태: ${modalRoot}`); return null; }
    if (!isOpen) return null;


    return ReactDOM.createPortal(
        <div className="modal-overlay" style={{ position: 'fixed', top: '20%', left: '30%', backgroundColor: 'white', padding: '20px' }}>
            <div className="modal-content" style={{ position: 'fixed', top: '20%', left: '30%', backgroundColor: 'white', padding: '20px' }}>
                <button onClick={closeModal}>닫기</button>
                {children}
            </div>
        </div>,
        modalRoot
    );
};

export default Modal;
