import React, { useEffect,useState } from 'react';
// import TermPage from './termPage';
import DirectoryViewer from './fileSystem';
import { Terminal } from "@xterm/xterm";
import "@xterm/xterm/css/xterm.css";


const MainComponent = () => {
  const [terminalText, setTerminalText] = useState('');
  const [guiState, setGUIState] = useState('');
  const term = new Terminal();
  const element = document.getElementById('terminal');

  const handleGUIEvent = (text) => {
    setTerminalText((prevText) => `${prevText}\n${text}`);
  };

  const handleTerminalInput = (input) => {
    // 특정 텍스트 입력 감지 및 GUI 상태 변경
    if (input === '/changeGUI') {
      setGUIState('updated');
    }
  };
  return (
    <>
        <DirectoryViewer command={guiState} />
        <TermPage
        term={term}
        element={element}
        onTerminalInput={handleTerminalInput}/>
    </>
  );
};

export default MainComponent;

import { SocketResult } from "./socket";
const TermPage = ({ term, element, onTerminalInput }) => {
  const [terminal, setTerminal] = useState(null);

  useEffect(() => {
    if (!terminal && element) {
      try {
        const newTerminal = term;
        newTerminal.onData((data) => {
          onTerminalInput(data);
        });
        newTerminal.open(element);
        setTerminal(newTerminal);

        const sockets = new SocketResult();
        let currentInput = "";
        let currentPath = "/home/user";

        newTerminal.write("Welcome to NetRunner!\r\n");
        prompt(newTerminal, currentPath);

        newTerminal.onKey(({ key, domEvent }) => {
          const char = key;
          if (domEvent.keyCode === 13) {
            // Enter key
            const message = currentInput;
            newTerminal.writeln("");
            sockets.sendMessage(message, currentPath);
            prompt(newTerminal, currentPath);
            sockets.getMessage((chat) => {
              newTerminal.write(chat);
              newTerminal.writeln("");
              prompt(newTerminal, currentPath);
            });
            currentInput = "";
          } else if (domEvent.keyCode === 8) {
            // Backspace 처리
            if (currentInput.length > 0) {
              newTerminal.write("\b \b"); // 터미널에서 문자를 제거
              currentInput = currentInput.slice(0, -1);
            }
          } else {
            currentInput += char;
            newTerminal.write(char);
          }
        });

        return () => {
          newTerminal.dispose();
        };
      } catch (error) {
        console.error("Error initializing terminal:", error);
      }
    }
  }, [term, element, onTerminalInput, terminal]);

  return <div id="terminal"  style={{ width: "100%", height: "100%" }} />;
};

function prompt(terminal, location) {
  if (location === "/root") {
    location = "~";
  }
  terminal.write(`root@root[${location}]$`);
}
