import { React, useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from "./loading.module.css";

function Loading() {
    const bar = useRef(null);
    const [percentage, setPercentage] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        const interval = setInterval(() => {
            setPercentage((prevPercentage) => {
                if (prevPercentage < 1) {
                    return prevPercentage + 0.01;
                }

                clearInterval(interval);
                return prevPercentage;
            });
        }, 50);

        return () => clearInterval(interval);
    }, []);

    useEffect(() => {
        if (percentage >= 1) {
            bar.current.classList.add("done");
            setTimeout(() => {
                navigate('/main');
            }, 2000);
        }
    }, [percentage, navigate]);

    return (
        <div ref={bar} className={styles.progress_bar}>
            <div className={styles.box}>
                <p className={styles.loading_text}>ë¡œë”©ì¤‘...</p>
                <p className={styles.done_text}>ë¡œë”© ì™„ë£Œ ðŸŽ‰</p>
                <div className={styles.box_front}></div>
                <div className={styles.box_bottom}></div>
            </div>
        </div>
    );
}

function load() {
    const bar = document.getElementsByClassName("progress_bar")[0];
    let percentage = 0;
    bar.style = `--progress: ${percentage}`;
    const interval = setInterval(() => {
        bar.style = `--progress: ${percentage}`;
        percentage += 0.01;
        if (percentage > 1) {
            clearInterval(interval);
            bar.classList.add("done");
            bar.style = `--progress: 1`;
            setTimeout(() => {
                useNavigate('/main');
            }, 2000);
        }
    }, 50);
}

export default Loading;
