import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styles from '../login.module.css';

function Modal() {
    let { id } = useParams();
    let navigate = useNavigate();

    const handleClose = () => {
        navigate(-1); // 이전 페이지로 돌아갑니다.
    };

    return (
        <div className={styles.modal} id="passwordResetModal">
            <div className={styles.modal_content}>
                <span className={styles.close}>&times;</span>
                <h2>비밀번호 찾기</h2>
                <form id="passwordResetForm" onSubmit="return validatePasswordResetForm(event)">
                    <div className={styles.inputBx}>
                        <input type="email" name="email" placeholder="이메일" required />
                        <input type="button" value="인증" id="verifyEmailButton" onClick="requestEmailVerification()" />
                    </div>
                    <div className={styles.inputBx} id="verificationCodeBox" >
                        <input type="text" name="verificationCode" placeholder="인증번호" required />
                        <input type="button" value="인증번호 확인" id="checkVerificationCodeButton" />
                    </div>
                    <div className={styles.inputBx} id="newPasswordBox">
                        <input type="password" name="newPassword" placeholder="새 비밀번호" required />
                        <input type="password" name="confirmNewPassword" placeholder="새 비밀번호 확인" required />
                        <input type="submit" value="비밀번호 재설정" id="resetPasswordButton" />
                    </div>
                </form>
            </div>
            <p>포스트 ID: {id}</p>
            <button onClick={handleClose}>닫기</button>
        </div>
    );
}

export default Modal;
