import { Terminal } from "@xterm/xterm";
import "../../node_modules/@xterm/xterm/css/xterm.css";
import React, { useRef, useEffect, useState } from "react";
import {SocketResult} from "./socket";

const TermPage = ({text,onTerminalInput}) => {
  const terminalRef = useRef(); // DOM 요소 참조를 위한 ref
  const refs = useRef();
  useEffect(() => {
    if (terminalRef.current) {
      terminalRef.current = new Terminal();
      terminalRef.current.onData((data) => {
        onTerminalInput(data);
      });
      let message = '';
      const sockets = new SocketResult();
      terminalRef.current.open(refs.current);
      let currentInput = '';
      terminalRef.current.write('Welcome to NetRunner!\r\n');
      let currentPath = '/home/user';
      terminalRef.current('',currentPath);
      terminalRef.current.onKey(({ key, domEvent }) => {
          const char = key;
          if (domEvent.keyCode === 13) {  // Enter key
              message = currentInput;
              terminalRef.current.writeln('');
              sockets.sendMessage(message,currentPath);
              prompt('',currentPath);
              sockets.getMessage((chat) => {
                  terminalRef.current.write(chat);
                  terminalRef.current.writeln('');
                  prompt('',currentPath)
                  chat= '';
              });
              currentInput = '';


          } else if (domEvent.keyCode === 8) {
              // Backspace 처리
              if (currentInput.length > 0) {
                  terminalRef.current.write('\b \b'); // 터미널에서 문자를 제거
                  currentInput = currentInput.slice(0, -1);
              }
          } else {
              currentInput += char;
              terminalRef.current.write(char);
          }
      });
      function prompt(ip, location) {
          if (location === "/root") {
              location = '~';
          }
          terminalRef.current.write(`${ip}root@root[${location}]$`);
      }
      return () => {
        terminalRef.current.dispose();
      };
    }

    if (text) {
      terminalRef.current.write(text);
      terminalRef.current.writeln('');
    }
  }, [text,onTerminalInput]);
  return <div ref={refs} style={{ width: "100%", height: "100%" }}></div>;
}
export default TermPage;
